sql.where:
STATUS != -1

	AND id = #{id}


	AND username LIKE '%${username}%'


	AND email LIKE '%${email}%'


	AND creator = #{creator}


	AND status = #{status}


	AND theme = #{theme}

mapper.select:
select u1.*,u2.id as creator,u2.username as creatorName
from
(
	SELECT u0.*,rownum as n
	FROM
	(
		SELECT *
		FROM admins

		ORDER BY ${sort} ${order}
	) u0
	WHERE rownum <=#{end}
) u1,admins u2
where u1.n >= #{start} AND u1.creator = u2.id(+)
