mapper.select:
SELECT T0.*
FROM 
(
   SELECT t1.*
    FROM 
        (
          SELECT GI0.*,F0.PATH,ROWNUM AS N
         FROM GOODS G0,FILES F0,GOODS_IMGS GI0
         WHERE G0.ID = GI0.OWNER AND GI0.IMG = F0.ID and g0.id = #{goodsId}

			AND gi0.id = #{id}

        ) t1

WHERE t1.N <=#{end}
) T0
WHERE T0.N >=#{start}

SELECT count(*)
FROM GOODS G0,GOODS_IMGS GI0
WHERE G0.ID = GI0.OWNER and g0.id = #{goodsId}

	AND gi0.id = #{id}
