mapper.select:
SELECT p4.*, CASE when p3.n <=0 then 'open' when p3.n >0 then 'closed' end AS STATE FROM ( SELECT p0.id,COUNT(p2.id) AS n
FROM ( SELECT p1.* FROM PERMISSIONS p1
p1.pid is null
p1.pid=#{value}
) p0, PERMISSIONS p2
WHERE p0.ID = p2.pid(+)
GROUP BY (p0.ID)
) p3 ,PERMISSIONS p4
WHERE p3.id = p4.ID
